			} else if (collision.gameObject.name == "Strike") {
				this.ResetBall();
				this.Player.Score -= 1;
			} else if (collision.gameObject.name == "Ground" && beenHit) {
				this.ResetBall();
				this.Player.Score -= 1;
			}
		}
